<?phpif(!defined('IN_DISCUZ') || !defined('IN_ADMINCP')) {	exit('Access Denied');}$plugin_ck8_pay_log = DB::table('ck8_pay_log');$sql = <<<EOFDROP TABLE IF EXISTS $plugin_ck8_pay_log;EOF;runquery($sql);function ck8_file($directory, $empty = false) {    if(substr($directory,-1) == "/") {        $directory = substr($directory,0,-1);    }    if(!file_exists($directory) || !is_dir($directory)) {        return false;    } elseif(!is_readable($directory)) {        return false;    } else {        @$directoryHandle = opendir($directory);        while ($contents = @readdir($directoryHandle)) {            if($contents != '.' && $contents != '..') {                $path = $directory . "/" . $contents;                if(is_dir($path)) {                    @ck8_file($path, $empty);                } else {                    @unlink($path);                }            }        }        @closedir($directoryHandle);        if($empty == false) {            if(!@rmdir($directory)) {                return false;            }        }        return true;    }}ck8_file(DISCUZ_ROOT.'./source/plugin/ck8_pay');$finish = TRUE;?>